:root {
  --bg-color: #121212;
  --card-bg: #1c1c1e;
  --primary-purple: #5849eb;
  --text-white: #ffffff;
  --text-grey: #8e8e93;
  --text-red: #ff453a;
  --text-green: #30d158;
  --font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
}

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  background-color: #000;
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  font-family: var(--font-family);
}

.mobile-frame {
  width: 100%;
  max-width: 375px;
  height: 812px;
  background-color: var(--bg-color);
  background: linear-gradient(180deg, #2a1f2d 0%, #121212 20%);
  position: relative;
  overflow: hidden;
  color: var(--text-white);
  display: flex;
  flex-direction: column;
  border-radius: 40px;
  box-shadow: 0 0 50px rgba(0,0,0,0.5);
}

/* Status Bar */
.status-bar {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 14px 24px 0;
  font-size: 14px;
  font-weight: 600;
  z-index: 10;
}

.status-icons {
  display: flex;
  align-items: center;
  gap: 4px;
}

/* Header */
.app-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 16px 20px;
  margin-top: 4px;
}

.menu-btn {
  background: none;
  border: none;
  cursor: pointer;
  padding: 4px;
}

.hamburger {
  width: 18px;
  display: flex;
  flex-direction: column;
  gap: 5px;
}

.hamburger span {
  display: block;
  width: 100%;
  height: 2px;
  background-color: white;
  border-radius: 2px;
}

.wallet-title {
  font-size: 16px;
  font-weight: 500;
}

.wallet-address {
  color: var(--text-grey);
  font-weight: 400;
}

.header-spacer {
  width: 24px;
}

/* Main Content */
.scroll-content {
  flex: 1;
  overflow-y: auto;
  padding: 0 16px;
  padding-bottom: 80px;
}

/* Balance */
.balance-section {
  text-align: center;
  padding: 24px 0 32px;
}

.balance-amount {
  font-size: 42px;
  font-weight: 700;
  margin-bottom: 8px;
  letter-spacing: -0.5px;
}

.balance-change {
  font-size: 15px;
  font-weight: 500;
}

.balance-change.negative {
  color: var(--text-red);
}

/* Actions */
.actions-section {
  display: flex;
  gap: 16px;
  margin-bottom: 24px;
}

.action-btn {
  flex: 1;
  background-color: var(--primary-purple);
  color: white;
  border: none;
  padding: 16px;
  border-radius: 28px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: opacity 0.2s;
}

.action-btn:active {
  opacity: 0.8;
}

/* Token List */
.token-list {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.token-card {
  background-color: var(--card-bg);
  border-radius: 16px;
  padding: 16px;
  display: flex;
  align-items: center;
  gap: 12px;
}

.token-icon {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  position: relative;
  overflow: hidden;
}

/* Custom Icons */
.solana-icon {
  background: #000;
  border: 1px solid #333;
}
.sol-bar {
  height: 3px;
  width: 20px;
  border-radius: 2px;
  position: absolute;
  background: linear-gradient(90deg, #00ffa3, #dc1fff);
}
.sol-bar.b1 { top: 12px; transform: skewX(-20deg); }
.sol-bar.b2 { top: 18px; transform: skewX(20deg); }
.sol-bar.b3 { top: 24px; transform: skewX(-20deg); }

.port-icon {
  background-color: #5e5ce6;
}

.saber-icon {
  background-color: #352a68;
  border: 1px solid #5e5ce6;
}
.saber-s {
  font-weight: 900;
  font-size: 24px;
  color: #5e5ce6;
  font-style: italic;
}

.invictus-icon {
  background-color: #000;
  border: 1px solid #333;
}
.owl-eyes {
  display: flex;
  gap: 4px;
}
.eye {
  width: 8px;
  height: 8px;
  border: 2px solid #ffd700;
  border-radius: 50%;
}
.beak {
  position: absolute;
  bottom: 10px;
  width: 6px;
  height: 4px;
  background-color: #ffd700;
  clip-path: polygon(0 0, 100% 0, 50% 100%);
}

.token-info {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.token-name {
  font-size: 15px;
  font-weight: 600;
}

.token-amount {
  font-size: 13px;
  color: var(--text-grey);
}

.token-value-group {
  text-align: right;
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.token-value {
  font-size: 15px;
  font-weight: 600;
}

.token-change {
  font-size: 13px;
  font-weight: 500;
}

.token-change.negative {
  color: var(--text-red);
}

.token-change.positive {
  color: var(--text-green);
}

/* Manage Link */
.manage-link {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--text-grey);
  font-size: 14px;
  padding: 24px 0;
  cursor: pointer;
}

/* Bottom Nav */
.bottom-nav {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 85px;
  background-color: #121212;
  display: flex;
  justify-content: space-around;
  align-items: flex-start;
  padding-top: 12px;
  border-top: 1px solid #1c1c1e;
}

.nav-item {
  position: relative;
  width: 50px;
  height: 50px;
  display: flex;
  justify-content: center;
  align-items: center;
  color: var(--text-grey);
  text-decoration: none;
}

.nav-item.active {
  color: white;
}

.active-indicator {
  position: absolute;
  top: -13px;
  width: 40px;
  height: 2px;
  background-color: #9d95ff;
  box-shadow: 0 0 10px #9d95ff;
  border-radius: 2px;
}

.nav-icon {
  font-size: 20px;
  font-weight: 700;
  display: flex;
  align-items: center;
  justify-content: center;
}

.home-indicator {
  position: absolute;
  bottom: 8px;
  left: 50%;
  transform: translateX(-50%);
  width: 134px;
  height: 5px;
  background-color: white;
  border-radius: 100px;
  z-index: 20;
}

/* Scrollbar hide */
::-webkit-scrollbar {
  display: none;
}
