/* CSS is included in the HTML file for portability, but here is the extracted CSS for reference */

:root {
    --color-orange: #F2994A;
    --color-blue: #2F80ED;
    --color-red: #EB5757;
    --color-green: #6FCF97;
    --color-text: #111111;
    --color-text-light: #555555;
    --bg-loom: #E5E5F7;
    --loom-button: #5865F2;
}

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: 'Inter', sans-serif;
    background-color: #ffffff;
    color: var(--color-text);
    line-height: 1.5;
    padding: 40px;
    display: flex;
    justify-content: center;
}

.container {
    max-width: 1000px;
    width: 100%;
    position: relative;
}

/* Header */
header {
    text-align: center;
    margin-bottom: 40px;
}

h1 {
    font-size: 36px;
    font-weight: 800;
    line-height: 1.2;
    margin-bottom: 20px;
}

.divider-dashed {
    border: 0;
    border-bottom: 2px dashed #ccc;
    width: 60%;
    margin: 0 auto;
}

/* Main Layout */
.main-content {
    display: grid;
    grid-template-columns: 1.8fr 1fr;
    gap: 40px;
}

.examples-column {
    display: flex;
    flex-direction: column;
    gap: 40px;
}

/* Top Example: Flex Tape */
.flex-tape-section {
    position: relative;
    padding: 60px 0 40px 0;
}

.images-row {
    display: flex;
    gap: 10px;
    justify-content: center;
    margin-bottom: 20px;
}

.image-placeholder {
    width: 120px;
    height: 100px;
    background-color: #eee;
    border: 1px solid #ddd;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
    position: relative;
}

.image-placeholder img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    opacity: 0.8;
}

/* Annotations */
.annotation {
    position: absolute;
    text-align: center;
    font-size: 13px;
    width: 140px;
    z-index: 10;
}

.annotation strong {
    display: block;
    font-weight: 700;
    margin-bottom: 2px;
}

.annotation span {
    color: var(--color-text);
    font-weight: 500;
    line-height: 1.2;
    display: block;
}

/* Specific Annotation Positions & Colors */
.anno-capability {
    color: var(--color-orange);
    top: 0;
    left: 20%;
}
.anno-capability::after {
    content: '';
    display: block;
    width: 2px;
    height: 40px;
    background: var(--color-orange);
    margin: 5px auto 0;
    transform: rotate(-15deg);
}
.anno-capability::before {
     /* Arrowhead */
    content: '';
    position: absolute;
    bottom: -40px;
    left: 50%;
    border-left: 5px solid transparent;
    border-right: 5px solid transparent;
    border-top: 8px solid var(--color-orange);
    transform: translateX(-8px) rotate(-15deg);
}

.anno-benefit {
    color: var(--color-blue);
    top: 0;
    right: 20%;
}
.anno-benefit::after {
    content: '';
    display: block;
    width: 2px;
    height: 40px;
    background: var(--color-blue);
    margin: 5px auto 0;
    transform: rotate(10deg);
}
.anno-benefit::before {
    content: '';
    position: absolute;
    bottom: -40px;
    left: 50%;
    border-left: 5px solid transparent;
    border-right: 5px solid transparent;
    border-top: 8px solid var(--color-blue);
    transform: translateX(2px) rotate(10deg);
}

.anno-feature {
    color: var(--color-green);
    bottom: -20px;
    left: 5%;
}
.anno-feature::before {
    content: '';
    display: block;
    width: 2px;
    height: 30px;
    background: var(--color-green);
    margin: 0 auto 5px;
}
.anno-feature::after {
    content: '';
    position: absolute;
    top: -5px;
    left: 50%;
    border-left: 5px solid transparent;
    border-right: 5px solid transparent;
    border-bottom: 8px solid var(--color-green);
    transform: translateX(-50%);
}

.anno-problem {
    color: var(--color-red);
    bottom: 0px;
    left: 50%;
    transform: translateX(-50%);
}
.anno-problem::before {
    content: '';
    display: block;
    width: 2px;
    height: 40px;
    background: var(--color-red);
    margin: 0 auto 5px;
    transform: rotate(15deg) translateY(-10px);
}
.anno-problem::after {
    content: '';
    position: absolute;
    top: -15px;
    left: 50%;
    border-left: 5px solid transparent;
    border-right: 5px solid transparent;
    border-bottom: 8px solid var(--color-red);
    transform: translateX(4px) rotate(15deg);
}

/* Divider */
.section-divider {
    border: 0;
    border-top: 1px solid #000;
    margin: 20px 0 40px 0;
}

/* Bottom Example: Loom */
.loom-section {
    display: flex;
    gap: 30px;
    align-items: center;
}

.loom-labels {
    display: flex;
    flex-direction: column;
    gap: 15px;
    text-align: right;
    font-size: 13px;
    min-width: 120px;
}

.loom-label-item strong {
    display: block;
    font-weight: 700;
}
.loom-label-item span {
    color: var(--color-text);
    font-weight: 500;
}

.loom-card {
    background-color: var(--bg-loom);
    padding: 30px;
    border: 1px solid #000;
    flex-grow: 1;
    display: flex;
    flex-direction: column;
    gap: 15px;
    position: relative;
}

.loom-card h2 {
    font-size: 32px;
    font-weight: 800;
    line-height: 1.1;
    color: #1A1A1A;
}

.loom-card p {
    font-size: 14px;
    line-height: 1.4;
    color: #333;
    max-width: 90%;
}

.loom-btn {
    background-color: var(--loom-button);
    color: white;
    border: none;
    padding: 12px 24px;
    border-radius: 50px;
    font-weight: 600;
    font-size: 14px;
    align-self: flex-start;
    margin-top: 10px;
    cursor: pointer;
}

/* Underlines in Loom Card */
.u-orange { border-bottom: 2px solid var(--color-orange); }
.u-blue { border-bottom: 2px solid var(--color-blue); }
.u-green { border-bottom: 2px solid var(--color-green); }

/* Right Column: List */
.summary-column {
    display: flex;
    flex-direction: column;
    justify-content: center;
    position: relative;
    padding-left: 20px;
}

.summary-content h3 {
    font-size: 18px;
    font-weight: 700;
    margin-bottom: 15px;
}

.summary-content ul {
    list-style: none;
}

.summary-content li {
    margin-bottom: 10px;
    font-size: 16px;
    position: relative;
    padding-left: 15px;
}

.summary-content li::before {
    content: '•';
    position: absolute;
    left: 0;
    font-weight: bold;
}

/* Curved Arrows */
.arrow-svg {
    position: absolute;
    width: 100px;
    height: 100px;
    fill: none;
    stroke: #000;
    stroke-width: 1.5;
    stroke-linecap: round;
}

.arrow-top {
    top: 20%;
    left: -60px;
    transform: rotate(-10deg);
}

.arrow-bottom {
    bottom: 20%;
    left: -60px;
    transform: rotate(10deg) scaleY(-1);
}

/* Footer */
.footer-logo {
    position: absolute;
    bottom: 0;
    right: 0;
    font-weight: 700;
    display: flex;
    align-items: center;
    gap: 5px;
    font-size: 14px;
}
.footer-square {
    width: 12px;
    height: 12px;
    background: #F2C94C;
    border: 1px solid #000;
}

/* Responsive */
@media (max-width: 768px) {
    .main-content {
        grid-template-columns: 1fr;
    }
    .summary-column {
        order: -1;
        padding-bottom: 40px;
        align-items: center;
    }
    .arrow-svg {
        display: none;
    }
    .loom-section {
        flex-direction: column;
        align-items: flex-start;
    }
    .loom-labels {
        text-align: left;
        flex-direction: row;
        flex-wrap: wrap;
    }
}
