:root {
  --bg-color: #000000;
  --card-bg: #111111;
  --text-primary: #FFFFFF;
  --text-secondary: #8E8E93;
  --accent-peach: #FFDCC4;
  --accent-lavender: #EBC7FF;
  --accent-pink: #FFC4CD;
  --font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
}

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  background-color: #1a1a1a;
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  font-family: var(--font-family);
  color: var(--text-primary);
}

.screen {
  width: 100%;
  max-width: 375px;
  height: 812px;
  background-color: var(--bg-color);
  position: relative;
  overflow: hidden;
  border-radius: 40px;
  box-shadow: 0 0 50px rgba(0,0,0,0.5);
  display: flex;
  flex-direction: column;
}

/* Status Bar */
.status-bar {
  height: 44px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 24px;
  font-size: 14px;
  font-weight: 600;
  padding-top: 10px;
}

.status-icons {
  display: flex;
  align-items: center;
  gap: 6px;
}

.battery {
  width: 24px;
  height: 12px;
  border: 1px solid rgba(255,255,255,0.3);
  border-radius: 3px;
  padding: 1px;
  position: relative;
  margin-left: 6px;
}

.battery-level {
  width: 80%;
  height: 100%;
  background-color: white;
  border-radius: 1px;
}

.battery::after {
  content: '';
  position: absolute;
  right: -3px;
  top: 3px;
  width: 2px;
  height: 4px;
  background-color: rgba(255,255,255,0.3);
  border-radius: 0 1px 1px 0;
}

/* Header */
.app-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 24px;
  margin-top: 10px;
}

.app-header h1 {
  font-size: 32px;
  font-weight: 700;
}

.new-btn {
  background-color: #2C2C2E;
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
}

/* Gauge Section */
.gauge-section {
  position: relative;
  height: 240px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 10px;
}

.gauge-wrapper {
  position: relative;
  width: 300px;
  height: 180px;
}

.gauge-svg {
  width: 100%;
  height: 100%;
  overflow: visible;
}

.curve-text {
  text-transform: uppercase;
  letter-spacing: 1px;
  font-size: 10px;
  fill: #666;
}

.gauge-content {
  position: absolute;
  top: 55%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
}

.gauge-content .amount {
  font-size: 42px;
  font-weight: 600;
  margin-bottom: 4px;
}

.gauge-content .label {
  color: var(--text-secondary);
  font-size: 14px;
}

.gauge-limits {
  position: absolute;
  bottom: 10px;
  width: 100%;
  display: flex;
  justify-content: space-between;
  padding: 0 30px;
  color: #444;
  font-size: 12px;
  font-weight: 500;
}

/* Budget List */
.budget-list {
  padding: 0 20px;
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.budget-card {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px;
  background-color: var(--card-bg);
  border-radius: 20px;
}

.card-left {
  display: flex;
  align-items: center;
  gap: 16px;
}

.icon-box {
  width: 48px;
  height: 48px;
  border-radius: 14px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 24px;
}

.text-group {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.title {
  font-size: 17px;
  font-weight: 600;
}

.subtitle {
  font-size: 13px;
  color: var(--text-secondary);
}

.card-right {
  text-align: right;
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.card-right .amount {
  font-size: 20px;
  font-weight: 500;
}

/* Bottom Navigation */
.bottom-nav {
  position: absolute;
  bottom: 30px;
  left: 0;
  width: 100%;
  padding: 0 30px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 80px;
}

.nav-item {
  background: none;
  border: none;
  cursor: pointer;
  padding: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
}

.add-btn {
  background-color: white;
  width: 60px;
  height: 48px;
  border-radius: 16px;
  display: flex;
  justify-content: center;
  align-items: center;
}

.home-indicator {
  position: absolute;
  bottom: 8px;
  left: 50%;
  transform: translateX(-50%);
  width: 134px;
  height: 5px;
  background-color: white;
  border-radius: 3px;
}
