:root {
  --primary-red: #FF385C;
  --text-main: #222222;
  --text-sub: #717171;
  --bg-color: #FFFFFF;
  --card-bg: #FFFFFF;
  --btn-bg: #F7F7F7;
  --shadow-soft: 0 6px 16px rgba(0,0,0,0.08);
  --shadow-card: 0 2px 8px rgba(0,0,0,0.08);
  --border-radius-card: 16px;
  --font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
}

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  background-color: #f0f0f0;
  font-family: var(--font-family);
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
}

.mobile-container {
  width: 375px;
  height: 812px;
  background-color: var(--bg-color);
  border-radius: 40px;
  position: relative;
  overflow: hidden;
  box-shadow: 0 0 0 12px #111, 0 20px 40px rgba(0,0,0,0.2);
  display: flex;
  flex-direction: column;
}

/* Status Bar */
.status-bar {
  height: 44px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 24px;
  font-size: 14px;
  font-weight: 600;
  z-index: 10;
}

.dynamic-island {
  width: 120px;
  height: 36px;
  background: #000;
  border-radius: 18px;
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
  top: 8px;
}

.status-icons {
  display: flex;
  align-items: center;
}

/* Header */
.app-header {
  padding: 12px 24px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: var(--bg-color);
  z-index: 5;
}

.app-header h1 {
  font-size: 26px;
  font-weight: 700;
  color: var(--text-main);
}

.icon-btn {
  background: #F7F7F7;
  border: none;
  width: 36px;
  height: 36px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: var(--text-main);
}

/* Content */
.content {
  flex: 1;
  overflow-y: auto;
  padding: 0 24px 80px 24px;
  /* Hide scrollbar */
  -ms-overflow-style: none;
  scrollbar-width: none;
}
.content::-webkit-scrollbar {
  display: none;
}

/* Cards */
.card {
  background: var(--card-bg);
  border-radius: var(--border-radius-card);
  box-shadow: var(--shadow-card);
  overflow: hidden;
  margin-bottom: 24px;
}

.booking-card {
  padding: 20px;
  margin-top: 12px;
  border: 1px solid #f0f0f0;
}

.card-header h2 {
  font-size: 20px;
  font-weight: 600;
  margin-bottom: 6px;
  color: var(--text-main);
}

.subtext {
  font-size: 14px;
  color: var(--text-sub);
  line-height: 1.4;
}

.card-footer {
  display: flex;
  justify-content: space-between;
  align-items: flex-end;
  margin-top: 24px;
}

.address p {
  font-size: 14px;
  color: var(--text-sub);
  line-height: 1.4;
}

.btn-secondary {
  background: var(--btn-bg);
  border: none;
  padding: 12px 16px;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 600;
  color: var(--text-main);
  cursor: pointer;
}

/* Timeline */
.timeline {
  position: relative;
  padding-left: 0;
}

.timeline-line {
  position: absolute;
  left: 24px;
  top: 20px;
  bottom: 0;
  width: 1px;
  background-color: #E0E0E0;
  z-index: 0;
}

.timeline-item {
  display: flex;
  margin-bottom: 24px;
  position: relative;
  z-index: 1;
}

.timeline-marker {
  width: 50px;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-right: 16px;
  flex-shrink: 0;
  padding-top: 4px;
}

.timeline-marker.active {
  background: var(--primary-red);
  color: white;
  width: 48px;
  height: 48px;
  border-radius: 50%;
  justify-content: center;
  box-shadow: 0 4px 10px rgba(255, 56, 92, 0.3);
  padding-top: 0;
}

.timeline-marker:not(.active):not(.placeholder) {
  background: #F0F0F0;
  color: var(--text-main);
  width: 48px;
  height: 48px;
  border-radius: 50%;
  justify-content: center;
  padding-top: 0;
}

.timeline-marker.placeholder {
  width: 48px;
  height: 10px;
  /* Invisible marker to keep alignment for items without dates */
}

.timeline-marker .day {
  font-size: 11px;
  font-weight: 600;
  text-transform: uppercase;
  line-height: 1;
  margin-bottom: 2px;
}

.timeline-marker .date {
  font-size: 16px;
  font-weight: 700;
  line-height: 1;
}

.timeline-content {
  flex: 1;
}

.event-card {
  margin-bottom: 0;
  border: 1px solid #f0f0f0;
}

.event-card.small {
  display: flex;
  align-items: center;
  padding: 12px;
}

.event-icon-box {
  width: 56px;
  height: 56px;
  background: #F5F0E6;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 16px;
  flex-shrink: 0;
}

.event-image-box {
  width: 56px;
  height: 56px;
  border-radius: 8px;
  margin-right: 16px;
  flex-shrink: 0;
  background-size: cover;
  background-position: center;
}

.food-bg {
  background-color: #4A5D45;
  background-image: linear-gradient(135deg, #4A5D45 0%, #2E3B2A 100%);
  position: relative;
}
.food-bg::after {
  content: 'ðŸŒ®';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: 24px;
}

.event-details h3 {
  font-size: 15px;
  font-weight: 600;
  color: var(--text-main);
  margin-bottom: 4px;
}

.event-card.large {
  display: flex;
  flex-direction: column;
}

.card-image {
  height: 160px;
  width: 100%;
  background-color: #222;
  position: relative;
}

.luchador-bg {
  background-image: linear-gradient(to bottom, rgba(0,0,0,0.1), rgba(0,0,0,0.6)), url('data:image/svg+xml;utf8,<svg width="100%" height="100%" xmlns="http://www.w3.org/2000/svg"><rect width="100%" height="100%" fill="%23222"/></svg>');
  background-size: cover;
}
.luchador-bg::after {
  content: 'ðŸ¤¼';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: 64px;
  opacity: 0.8;
}

.card-body {
  padding: 16px;
}

.card-body h3 {
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 6px;
  line-height: 1.3;
}

/* Bottom Navigation */
.bottom-nav {
  height: 80px;
  background: #FFFFFF;
  border-top: 1px solid #EBEBEB;
  display: flex;
  justify-content: space-around;
  align-items: flex-start;
  padding-top: 12px;
  position: absolute;
  bottom: 0;
  width: 100%;
  z-index: 20;
}

.nav-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  color: #B0B0B0;
  font-size: 10px;
  font-weight: 500;
  cursor: pointer;
  width: 20%;
}

.nav-item svg {
  margin-bottom: 4px;
  color: #B0B0B0;
}

.nav-item.active {
  color: var(--primary-red);
}

.nav-item.active svg {
  color: var(--primary-red);
}

.airbnb-logo {
  margin-bottom: 4px;
}
