/* CSS is included in the HTML file for portability, but here is the extracted CSS for reference */
:root {
    --bg-color: #FDFBF9;
    --text-primary: #1A1A1A;
    --text-secondary: #888888;
    --accent-blue: #CDE4F5;
    --accent-teal: #1A4D4E;
    --accent-coral: #FF8A80;
    --accent-green: #C5E1A5;
    --font-serif: 'Playfair Display', serif;
    --font-sans: 'DM Sans', sans-serif;
}

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    background-color: #111;
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    font-family: var(--font-sans);
}

.phone-frame {
    width: 375px;
    height: 812px;
    background-color: var(--bg-color);
    border-radius: 40px;
    position: relative;
    overflow: hidden;
    box-shadow: 0 0 0 12px #2a2a2a, 0 20px 50px rgba(0,0,0,0.5);
    display: flex;
    flex-direction: column;
}

/* Notch */
.notch {
    position: absolute;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 120px;
    height: 30px;
    background-color: #000;
    border-bottom-left-radius: 16px;
    border-bottom-right-radius: 16px;
    z-index: 100;
}

/* Content Padding */
.content {
    padding: 60px 24px 20px;
    flex: 1;
    overflow-y: auto;
    scrollbar-width: none;
}
.content::-webkit-scrollbar {
    display: none;
}

/* Header */
header {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin-bottom: 32px;
}

.header-left {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.date {
    font-size: 10px;
    letter-spacing: 1px;
    color: var(--text-secondary);
    text-transform: uppercase;
    font-weight: 500;
}

.greeting {
    font-size: 11px;
    line-height: 1.4;
    letter-spacing: 0.5px;
    color: var(--text-primary);
    text-transform: uppercase;
    font-weight: 700;
}

.weather {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 4px;
}

.sun-icon {
    width: 24px;
    height: 24px;
    border: 1.5px solid var(--text-primary);
    border-radius: 50%;
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
}
.sun-icon::after {
    content: '';
    width: 8px;
    height: 8px;
    background: var(--text-primary);
    border-radius: 50%;
}
.sun-rays {
    position: absolute;
    width: 100%;
    height: 100%;
    animation: spin 10s linear infinite;
}
.sun-rays span {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 2px;
    height: 4px;
    background: var(--text-primary);
    transform-origin: center;
}
/* Creating rays using transforms */
.sun-rays span:nth-child(1) { transform: translate(-50%, -16px); }
.sun-rays span:nth-child(2) { transform: translate(-50%, 12px); }
.sun-rays span:nth-child(3) { transform: translate(-50%, -50%) rotate(90deg) translate(0, -14px); }
.sun-rays span:nth-child(4) { transform: translate(-50%, -50%) rotate(90deg) translate(0, 14px); }
.sun-rays span:nth-child(5) { transform: translate(-50%, -50%) rotate(45deg) translate(0, -14px); }
.sun-rays span:nth-child(6) { transform: translate(-50%, -50%) rotate(45deg) translate(0, 14px); }
.sun-rays span:nth-child(7) { transform: translate(-50%, -50%) rotate(-45deg) translate(0, -14px); }
.sun-rays span:nth-child(8) { transform: translate(-50%, -50%) rotate(-45deg) translate(0, 14px); }

.temp {
    font-size: 11px;
    color: var(--text-secondary);
}

/* Main Heading */
h1 {
    font-family: var(--font-serif);
    font-size: 32px;
    font-weight: 400;
    line-height: 1.1;
    color: var(--text-primary);
    margin-bottom: 32px;
}

/* Featured Plants */
.featured-plants {
    display: flex;
    justify-content: space-between;
    margin-bottom: 40px;
}

.plant-card {
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 48%;
}

.plant-circle {
    width: 140px;
    height: 140px;
    border-radius: 50%;
    margin-bottom: 16px;
    position: relative;
    overflow: hidden;
}

.plant-circle.junie {
    background: linear-gradient(135deg, #E3F2FD 0%, #BBDEFB 100%);
}
/* Abstract representation of Calla Lilies */
.plant-circle.junie::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 30px;
    width: 40px;
    height: 80px;
    background: #fff;
    border-radius: 50% 50% 50% 0;
    transform: rotate(-10deg);
    box-shadow: 40px 10px 0 -5px #fff;
}
.plant-circle.junie::before {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50px;
    width: 4px;
    height: 100px;
    background: #4CAF50;
    box-shadow: 40px 0 0 #4CAF50;
}

.plant-circle.gloria {
    background: linear-gradient(135deg, #1A4D4E 0%, #004D40 100%);
}
/* Abstract representation of Orchids */
.plant-circle.gloria::after {
    content: '';
    position: absolute;
    top: 40px;
    right: 30px;
    width: 30px;
    height: 30px;
    background: #F8BBD0;
    border-radius: 50%;
    box-shadow: -40px 20px 0 5px #F48FB1, -20px 50px 0 -2px #F8BBD0;
}

.plant-name {
    font-family: var(--font-serif);
    font-size: 18px;
    color: var(--text-primary);
}

/* Updates Section */
.updates-header {
    display: flex;
    align-items: center;
    gap: 8px;
    margin-bottom: 20px;
}

h2 {
    font-family: var(--font-serif);
    font-size: 24px;
    font-weight: 400;
    color: var(--text-primary);
}

.badge {
    background-color: #000;
    color: #fff;
    font-size: 10px;
    width: 16px;
    height: 16px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 700;
    transform: translateY(-4px);
}

/* Tags Scroller */
.tags-scroller {
    display: flex;
    gap: 8px;
    overflow-x: auto;
    padding-bottom: 8px;
    margin-bottom: 24px;
    scrollbar-width: none;
}
.tags-scroller::-webkit-scrollbar {
    display: none;
}

.tag {
    padding: 8px 16px;
    border-radius: 20px;
    font-size: 9px;
    font-weight: 700;
    letter-spacing: 0.5px;
    white-space: nowrap;
    border: none;
    cursor: pointer;
}

.tag.active {
    background-color: #000;
    color: #fff;
}

.tag.inactive {
    background-color: #EEEEEE;
    color: #666;
}

/* Bottom Plants Scroller */
.plants-scroller {
    display: flex;
    gap: 16px;
    overflow-x: auto;
    padding-bottom: 20px;
    scrollbar-width: none;
}
.plants-scroller::-webkit-scrollbar {
    display: none;
}

.avatar-wrapper {
    position: relative;
    flex-shrink: 0;
}

.avatar {
    width: 64px;
    height: 64px;
    border-radius: 50%;
    overflow: hidden;
    position: relative;
}

.avatar.one { background: #90CAF9; }
.avatar.one::after { 
    content: ''; position: absolute; top: 15px; left: 15px; width: 34px; height: 34px; 
    background: #D32F2F; border-radius: 50% 0 50% 50%; transform: rotate(45deg);
}

.avatar.two { background: #FFAB91; }
.avatar.two::after { 
    content: ''; position: absolute; top: 20px; left: 20px; width: 24px; height: 24px; 
    background: #BA68C8; border-radius: 50% 50% 0 50%; 
}

.avatar.three { background: #AED581; }
.avatar.three::after { 
    content: ''; position: absolute; top: 15px; left: 25px; width: 14px; height: 34px; 
    background: #fff; border-radius: 50%; transform: rotate(-10deg);
}

.avatar.four { background: #E1BEE7; }

.status-dot {
    position: absolute;
    top: 4px;
    right: 4px;
    width: 8px;
    height: 8px;
    border-radius: 50%;
    border: 1.5px solid var(--bg-color);
}
.status-dot.blue { background-color: #29B6F6; }
.status-dot.orange { background-color: #FF7043; }

/* Bottom Navigation */
nav {
    height: 80px;
    background-color: var(--bg-color);
    display: flex;
    justify-content: space-around;
    align-items: flex-start;
    padding-top: 16px;
    border-top: 1px solid rgba(0,0,0,0.03);
}

.nav-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 6px;
    color: #999;
    cursor: pointer;
}

.nav-item.active {
    color: #000;
}

.nav-icon {
    width: 24px;
    height: 24px;
    stroke: currentColor;
    fill: none;
    stroke-width: 1.5;
}

.nav-label {
    font-size: 8px;
    letter-spacing: 0.5px;
    text-transform: uppercase;
    font-weight: 600;
}
